import React, { useState, useEffect } from 'react';
import './Todomain.css';
import { TodoFilter } from './componentsTodo/TodoFilter';
import { TodoItem } from './componentsTodo/TodoItem';
import { TodoCounter } from './componentsTodo/TodoCounter';
import { useTasks } from './componentsTodo/useTask';
import { useDarkMode } from './componentsTodo/useDarkMode';

function Todomain() {
  const { tasks, isLoading, addTask, removeTask, toggleCompleted } = useTasks();
  const { isDarkMode, toggleDarkMode } = useDarkMode();

  if (isLoading) {
    return <div className="loading">Cargando tareas...</div>;
  }

  const totalTasks = tasks.length;
  const completedTasks = tasks.filter(task => task.completed).length;

  return (
    <div className={`App ${isDarkMode ? 'dark-mode' : ''}`}>
      <button className="dark-mode-button" onClick={toggleDarkMode}>Modo {isDarkMode ? 'Claro' : 'Oscuro'}</button>
      <TodoCounter total={totalTasks} completed={completedTasks} />
      <TodoFilter addTask={addTask} />
      <ul>
        {tasks.map((task) => (
          <TodoItem
            key={task.id}
            id={task.id}
            text={task.text}
            completed={task.completed}
            onToggleCompleted={toggleCompleted}
            onRemoveTask={removeTask}
          />
        ))}
      </ul>
    </div>
  );
}

export default Todomain;
