// TodoCounter.js

import React from 'react';
import noTasksImage from '../assets/images/no-tasks.png'; // Ajusta la ruta según sea necesario
import allDoneImage from '../assets/images/all-done.png'; // Ajusta la ruta según sea necesario

function TodoCounter({ total, completed }) {
    let content = (
        <h4>Tareas Completadas: {completed} de {total}</h4>
    );

    if (total === 0) {
        content = (
            <div>
                <img src={noTasksImage} alt="No tasks" />
                <p>No hay tareas pendientes.</p>
            </div>
        );
    } else if (completed === total && total > 0) {
        content = (
            <div>
                <img src={allDoneImage} alt="All tasks completed" />
                <p>¡Lo lograste, todas las tareas completadas!</p>
            </div>
        );
    }

    return (
        <div className="todo-counter">
            {content}
        </div>
    );
}

export { TodoCounter };
