// En Todomain.js

import React, { useState, useEffect } from 'react';
import './Todomain.css';
import { TodoFilter } from './componentsTodo/TodoFilter';
import { TodoItem } from './componentsTodo/TodoItem';
import { TodoCounter } from './componentsTodo/TodoCounter';
import { useTasks } from './componentsTodo/useTask';  // Asegúrate que el nombre del archivo importado es correcto
import { useDarkMode } from './componentsTodo/useDarkMode';

function Todomain() {
  const { tasks, addTask, removeTask, toggleCompleted } = useTasks();
  const { isDarkMode, toggleDarkMode } = useDarkMode();

  const [currentSearchText, setCurrentSearchText] = useState('');
  const [filteredTasks, setFilteredTasks] = useState([]);

   useEffect(() => {
    // Actualiza el estado de filteredTasks basado en el texto de búsqueda
    if (currentSearchText.trim()) {
      const searchResults = tasks.filter(task =>
        task.text.toLowerCase().includes(currentSearchText.toLowerCase())
      );
      setFilteredTasks(searchResults);
    } else {
      setFilteredTasks(tasks); // Restablece a todas las tareas cuando no hay texto
    }
  }, [currentSearchText, tasks]); // Depende de currentSearchText y tasks

  return (
    <div className={`App ${isDarkMode ? 'dark-mode' : ''}`}>
      <TodoCounter total={tasks.length} completed={tasks.filter(task => task.completed).length} />
      <TodoFilter addTask={addTask} searchTasks={setCurrentSearchText} />
      <ul>
        {filteredTasks.map((task) => (
          <TodoItem
            key={task.id}
            id={task.id}
            text={task.text}
            completed={task.completed}
            onToggleCompleted={toggleCompleted}
            onRemoveTask={removeTask}
          />
        ))}
      </ul>
    </div>
  );
}

export default Todomain;
